function translateDate1() {
    setTimeout(() => {
        const dateLangTd = document.querySelectorAll('td')
        for (let i = 0; i < dateLangTd.length; i++) {
            if (dateLangTd[i].getAttribute("data-column-id") === "effectiveDate") {
                dateLangTd[i].style.cssText = 'visibility: hidden'
                if (dateLangTd[i].querySelector('.dateLang') === null) {
                    let dateValue = dateLangTd[i].innerHTML
                    dateLangTd[i].innerHTML += `<div class="dateLang">${translateDate(dateValue)}</div>`
                }   
            }
        }

        const dateLang = document.querySelectorAll('.dateLang')
        for (let i = 0; i < dateLang.length; i++) {
            if (i % 2 !== 0) {
                dateLang[i].style.cssText = `background-color: #f2f2f2`
            } else {
                dateLang[i].style.cssText = ``
            }
        }
    }, 500)
}

