function checkUrlViewHist(string) {
    let currentURL = window.location.href
    if (string[0] === "/") {
        let urlArr = currentURL.split('/');
        urlArr.pop()

        let pathname = string.slice(1)
        urlArr.push(pathname)
        let urlToSend = urlArr.join('/')
        return urlToSend
    } else {
        return string
    }
}

